    public function testCountPrepositionsFromTextA()
    {
        $countPrepositions = $this->booglans->countPrepositions(static::$textA);
        echo "count prepositions TextA: " . $countPrepositions . PHP_EOL;
        $this->assertEquals($countPrepositions, 49);
    }

    public function testCountPrepositionsFromTextB()
    {
        $countPrepositions = $this->booglans->countPrepositions(static::$textB);
        echo "count prepositions TextB: " . $countPrepositions . PHP_EOL;
        $this->assertTrue($countPrepositions > 0);
    }

    public function testCountVerbsFromTextA()
    {
        $countVerbs = $this->booglans->countVerbs(static::$textA);
        echo "count verbs TextA: " . $countVerbs . PHP_EOL;
        $this->assertEquals($countVerbs, 71);
    }

    public function testCountVerbsFirstPersonFromTextA()
    {
        $countVerbsFirstPerson = $this->booglans->countVerbsFirstPerson(
            static::$textA
        );
        echo "count first person TextA: " . $countVerbsFirstPerson . PHP_EOL;
        $this->assertEquals($countVerbsFirstPerson, 58);
        #die('finish debug');
    }

    public function testCountVerbsFromTextB()
    {
        $countVerbs = $this->booglans->countVerbs(static::$textB);
        echo "count verbs TextB: " . $countVerbs . PHP_EOL;
        $this->assertTrue($countVerbs > 0);
    }

    public function testCountVerbsFirstPersonFromTextB()
    {
        $countVerbsFirstPerson = $this->booglans->countVerbsFirstPerson(
            static::$textB
        );
        echo "count first person TextB: " . $countVerbsFirstPerson . PHP_EOL;
        $this->assertTrue($countVerbsFirstPerson > 0);
    }

    public function testSortTextA()
    {
        $textSorted = $this->booglans->sortText(static::$textA);
        echo "Sorted TextA: " . $textSorted . PHP_EOL . PHP_EOL;
        $this->assertEquals($textSorted, static::$textASorted);
    }

    public function testSortTextB()
    {
        $textSorted = $this->booglans->sortText(static::$textB);
        echo "Sorted TextB: " . $textSorted . PHP_EOL . PHP_EOL;
        $this->assertTrue(strlen($textSorted) > 0);
    }

    public function testCountPrettyNumbersTextA()
    {
        $countPrettyNumbers = $this->booglans->countPrettyNumbers(
            static::$textA
        );
        echo "count pretty number TextA: " . $countPrettyNumbers . PHP_EOL;
        $this->assertEquals($countPrettyNumbers, 140);
    }

    public function testCountPrettyNumbersTextB()
    {
        $countPrettyNumbers = $this->booglans->countPrettyNumbers(
            static::$textB
        );
        echo "count pretty number TextB: " . $countPrettyNumbers . PHP_EOL;
        $this->assertTrue($countPrettyNumbers > 0);
    }

    public function tearDown()
    {
        unset($this->booglans);
        static::$textA = static::$textB = null;
    }
